CREATE PROCEDURE SpActualizabRemision @empresa int,@funcionarioAsignado int,@fechaCreacion datetime,@fechaImpresion datetime,@fechaAsignacion datetime,@codigo varchar(50),@usuario varchar(50),@estado char(1),@Retorno int output  AS begin tran bRemision update bRemision set funcionarioAsignado = @funcionarioAsignado,fechaCreacion = @fechaCreacion,fechaImpresion = @fechaImpresion,fechaAsignacion = @fechaAsignacion,usuario = @usuario,estado = @estado where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran bRemision end else begin set @Retorno = 1 rollback tran bRemision end