CREATE PROCEDURE [dbo].[SpActualizacCentrosCosto] @empresa int,@nivel int,@nivelMayor int,@activo bit,@auxiliar bit,@codigo varchar(50),@mayor varchar(50),@descripcion varchar(350),@responsable varchar(150),@grupo varchar(50),@manejaHE bit, @manejaLC bit,@Retorno int output  AS begin tran cCentrosCosto update cCentrosCosto set nivel = @nivel,nivelMayor = @nivelMayor,activo = @activo,auxiliar = @auxiliar,mayor = @mayor,descripcion = @descripcion,responsable = @responsable,grupo = @grupo, manejaHE=@manejaHE, manejaLC=@manejaLC where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran cCentrosCosto end else begin set @Retorno = 1 rollback tran cCentrosCosto end