CREATE PROCEDURE SpActualizacGrupoIR @empresa int,@activo bit,@descripcion varchar(950),@observacion varchar(1550),@codigo char(5),@Retorno int output  AS begin tran cGrupoIR update cGrupoIR set activo = @activo,descripcion = @descripcion,observacion = @observacion where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran cGrupoIR end else begin set @Retorno = 1 rollback tran cGrupoIR end