CREATE PROCEDURE [dbo].[SpActualizacParametroContaNomi] @empresa int,@id int,@tipoDato int,@manejaEntidad bit,@valorTipoDato decimal(18,2),@tipo varchar(50),@clase varchar(50),@tipoTransaccion varchar(50),@cCostoMayor varchar(50),@cCosto varchar(50),@departamento varchar(50),@concepto varchar(50),@cuentaActivo varchar(50),@cuentaGasto varchar(50),@cuentaContratista varchar(50),@cCostoMayorSigo varchar(50),@cCostoSigo varchar(50),@cuentaCredito varchar(50),@cCostoMayorCredito varchar(50),@cCostoCredito varchar(50),@entidad varchar(50),@tercero varchar(50), @terceroCredito  varchar(50),@Retorno int output  AS begin tran cParametroContaNomi update cParametroContaNomi set tipoDato = @tipoDato,manejaEntidad = @manejaEntidad,valorTipoDato = @valorTipoDato,tipo = @tipo,clase = @clase,tipoTransaccion = @tipoTransaccion,cCostoMayor = @cCostoMayor,cCosto = @cCosto,departamento = @departamento,concepto = @concepto,cuentaActivo = @cuentaActivo,cuentaGasto = @cuentaGasto,cuentaContratista = @cuentaContratista,cCostoMayorSigo = @cCostoMayorSigo,cCostoSigo = @cCostoSigo,cuentaCredito = @cuentaCredito,cCostoMayorCredito = @cCostoMayorCredito,cCostoCredito = @cCostoCredito,entidad = @entidad, tercero=@tercero, tercerocredito=@tercerocredito where empresa = @empresa and id = @id if (@@error = 0 ) begin set @Retorno = 0 commit tran cParametroContaNomi end else begin set @Retorno = 1 rollback tran cParametroContaNomi end