create PROCEDURE [dbo].[SpActualizacPlanCuenta] @empresa int,@activo bit,@codigo varchar(50),@descripcion varchar(550),@Retorno int output  AS begin tran cPlanCuenta update cPlanCuenta set activo = @activo,descripcion = @descripcion where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran cPlanCuenta end else begin set @Retorno = 1 rollback tran cPlanCuenta end