CREATE PROCEDURE SpActualizacxcClienteClaseIR @empresa int,@tercero int,@clase int,@cliente varchar(10),@concepto varchar(5),@Retorno int output  AS begin tran cxcClienteClaseIR update cxcClienteClaseIR set concepto = @concepto where clase = @clase and cliente = @cliente and empresa = @empresa and tercero = @tercero if (@@error = 0 ) begin set @Retorno = 0 commit tran cxcClienteClaseIR end else begin set @Retorno = 1 rollback tran cxcClienteClaseIR end