CREATE PROCEDURE SpActualizagEntidadNacional @empresa int,@codigo varchar(50),@descripcion varchar(550),@Retorno int output  AS begin tran gEntidadNacional update gEntidadNacional set descripcion = @descripcion where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran gEntidadNacional end else begin set @Retorno = 1 rollback tran gEntidadNacional end