CREATE PROCEDURE SpActualizagRegimenTributario @empresa int,@descripcion varchar(50),@codigo char(1),@Retorno int output  AS begin tran gRegimenTributario update gRegimenTributario set descripcion = @descripcion where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran gRegimenTributario end else begin set @Retorno = 1 rollback tran gRegimenTributario end