create PROCEDURE [dbo].[SpActualizagTipoEmbargo] @empresa int,@activo bit,@codigo varchar(50),@descripcion varchar(250),@Retorno int output  AS begin tran gTipoEmbargo update gTipoEmbargo set activo = @activo,descripcion = @descripcion where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran gTipoEmbargo end else begin set @Retorno = 1 rollback tran gTipoEmbargo end