CREATE PROCEDURE SpActualizagUnidadMedida @empresa int,@activo bit,@codigo varchar(50),@descripcion varchar(50),@desCorta char(3),@Retorno int output  AS begin tran gUnidadMedida update gUnidadMedida set activo = @activo,descripcion = @descripcion,desCorta = @desCorta where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran gUnidadMedida end else begin set @Retorno = 1 rollback tran gUnidadMedida end