CREATE PROCEDURE SpActualizaiGrupoImpuestoIR @empresa int,@observacion varchar(1550),@grupo char(5),@concepto char(5),@Retorno int output  AS begin tran iGrupoImpuestoIR update iGrupoImpuestoIR set observacion = @observacion,concepto = @concepto where empresa = @empresa and grupo = @grupo if (@@error = 0 ) begin set @Retorno = 0 commit tran iGrupoImpuestoIR end else begin set @Retorno = 1 rollback tran iGrupoImpuestoIR end