CREATE PROCEDURE SpActualizaiNivelDestino @empresa int,@activo bit,@codigo varchar(2),@descripcion varchar(250),@Retorno int output  AS begin tran iNivelDestino update iNivelDestino set activo = @activo,descripcion = @descripcion where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran iNivelDestino end else begin set @Retorno = 1 rollback tran iNivelDestino end