CREATE PROCEDURE [dbo].[SpActualizanConceptoRango] @empresa int,@registro int,@minimo money,@maximo money,@valor money,@por bit,@porcentaje decimal(18,3),@concepto varchar(50),@Retorno int output  AS begin tran nConceptoRango update nConceptoRango set minimo = @minimo,maximo = @maximo,valor = @valor,por = @por,porcentaje = @porcentaje where empresa = @empresa and concepto = @concepto and registro = @registro if (@@error = 0 ) begin set @Retorno = 0 commit tran nConceptoRango end else begin set @Retorno = 1 rollback tran nConceptoRango end