CREATE PROCEDURE [dbo].[SpActualizanEntidadCaja] @empresa int,@tercero int,@fechaRegistro datetime,@integral bit,@activo bit,@pAporte decimal(18,2),@codigo varchar(50),@descripcion varchar(550),@proveedor varchar(50),@codigoNacional varchar(50),@pais varchar(50),@ciudad varchar(50),@observacion varchar(5550),@usuario varchar(50),@cuenta varchar(50),@Retorno int output  AS begin tran nEntidadCaja update nEntidadCaja set tercero = @tercero,fechaRegistro = @fechaRegistro,integral = @integral,activo = @activo,pAporte = @pAporte,descripcion = @descripcion,proveedor = @proveedor,codigoNacional = @codigoNacional,pais = @pais,ciudad = @ciudad,observacion = @observacion,usuario = @usuario,cuenta = @cuenta where empresa = @empresa and codigo = @codigo if (@@error = 0 ) begin set @Retorno = 0 commit tran nEntidadCaja end else begin set @Retorno = 1 rollback tran nEntidadCaja end