
CREATE PROCEDURE [dbo].[SpActualizanEntidadIcbf] @empresa int,@tercero int,@fechaRegistro datetime,@integral bit,@activo bit,@pAporte decimal(18,3),@codigo varchar(50),@descripcion varchar(550),@proveedor varchar(50),@codigoNacional varchar(50),@observacion varchar(5550),@usuario varchar(50),@cuenta varchar(50),@pais varchar(10),@ciudad varchar(10),@Retorno int output  AS begin tran nEntidadIcbf 
update nEntidadIcbf set tercero = @tercero,fechaRegistro = @fechaRegistro,integral = @integral,activo = @activo,pAporte = @pAporte,descripcion = @descripcion,proveedor = @proveedor,codigoNacional = @codigoNacional,observacion = @observacion,usuario = @usuario,cuenta = @cuenta,pais = @pais,ciudad = @ciudad where empresa = @empresa and codigo = @codigo if (@@error = 0 ) begin set @Retorno = 0 commit tran nEntidadIcbf end else begin set @Retorno = 1 rollback tran nEntidadIcbf end