CREATE PROCEDURE [dbo].[SpActualizanNovedadesDetalle] @empresa int,@registro int,@cantidad int,@año int,@periodoInicial int,@periodoFinal int,@frecuencia int,@ultimoPeriodoLiquidado int,@ultimoPeriodoFrecuencia int,@valor money,@liquidada bit,@anulado bit,@tipo varchar(50),@numero varchar(50),@concepto varchar(50),@empleado varchar(50),@detalle varchar(250),@Retorno int output  AS begin tran nNovedadesDetalle update nNovedadesDetalle set cantidad = @cantidad,año = @año,periodoInicial = @periodoInicial,periodoFinal = @periodoFinal,frecuencia = @frecuencia,ultimoPeriodoLiquidado = @ultimoPeriodoLiquidado,ultimoPeriodoFrecuencia = @ultimoPeriodoFrecuencia,valor = @valor,liquidada = @liquidada,anulado = @anulado,concepto = @concepto,empleado = @empleado,detalle = @detalle where empresa = @empresa and tipo = @tipo and numero = @numero and registro = @registro if (@@error = 0 ) begin set @Retorno = 0 commit tran nNovedadesDetalle end else begin set @Retorno = 1 rollback tran nNovedadesDetalle end