 CREATE PROCEDURE [dbo].[SpActualizanParametroSeguridadSocial] @empresa int,@codigo varchar(50),@descripcion varchar(550),@Retorno int output  AS begin tran nParametroSeguridadSocial update nParametroSeguridadSocial set descripcion = @descripcion where empresa = @empresa and codigo = @codigo if (@@error = 0 ) begin set @Retorno = 0 commit tran nParametroSeguridadSocial end else begin set @Retorno = 1 rollback tran nParametroSeguridadSocial end