CREATE PROCEDURE [dbo].[SpActualizanParametrosAno] @empresa int,@ano int,@noSueldoST int,@vSalarioMinimo money,@vAuxilioTransporte money,@vUVT money,@vPatrimonioBruto money,@vIngresoBruto money,@vMaximoExento money,@vUVT3 money,@vUVT4 money,@vUVT5 money,@vUVT6 money,@vMinimoPeriodo money,@vMaxPagoSalud money,@vMaxAporteAFC money,@vMaxDeducibleVivienda money,@vDependientes money,@vMinimoingresosDeclarante money,@vUVT1 money,@vUVT2 money,@fechaRegistro datetime,@cAplicarArt385 bit,@cSalarioIntegral bit,@cRestaIncapacidad bit,@cDiasTNL bit,@pExentoRetencion decimal,@pMaximoaportePension decimal,@pExentoSalario1393 decimal,@pDependientes decimal,@usuario varchar(30),@observacion varchar(5000),@Retorno int output  AS begin tran nParametrosAno update nParametrosAno set noSueldoST = @noSueldoST,vSalarioMinimo = @vSalarioMinimo,vAuxilioTransporte = @vAuxilioTransporte,vUVT = @vUVT,vPatrimonioBruto = @vPatrimonioBruto,vIngresoBruto = @vIngresoBruto,vMaximoExento = @vMaximoExento,vUVT3 = @vUVT3,vUVT4 = @vUVT4,vUVT5 = @vUVT5,vUVT6 = @vUVT6,vMinimoPeriodo = @vMinimoPeriodo,vMaxPagoSalud = @vMaxPagoSalud,vMaxAporteAFC = @vMaxAporteAFC,vMaxDeducibleVivienda = @vMaxDeducibleVivienda,vDependientes = @vDependientes,vMinimoingresosDeclarante = @vMinimoingresosDeclarante,vUVT1 = @vUVT1,vUVT2 = @vUVT2,fechaRegistro = @fechaRegistro,cAplicarArt385 = @cAplicarArt385,cSalarioIntegral = @cSalarioIntegral,cRestaIncapacidad = @cRestaIncapacidad,cDiasTNL = @cDiasTNL,pExentoRetencion = @pExentoRetencion,pMaximoaportePension = @pMaximoaportePension,pExentoSalario1393 = @pExentoSalario1393,pDependientes = @pDependientes,usuario = @usuario,observacion = @observacion where empresa = @empresa and ano = @ano if (@@error = 0 ) begin set @Retorno = 0 commit tran nParametrosAno end else begin set @Retorno = 1 rollback tran nParametrosAno end