
create PROCEDURE [dbo].[SpActualizanParametrosTipoCotizante] @empresa int,@salud bit,@pension bit,@fondoSolidaridad bit,@arp bit,@caja bit,@sena bit,@icbf bit,@tipoCotizante varchar(50),@subTipoCotizante varchar(50),@Retorno int output  AS begin tran nParametrosTipoCotizante update nParametrosTipoCotizante set salud = @salud,pension = @pension,fondoSolidaridad = @fondoSolidaridad,arp = @arp,caja = @caja,sena = @sena,icbf = @icbf where empresa = @empresa and subTipoCotizante = @subTipoCotizante and tipoCotizante = @tipoCotizante if (@@error = 0 ) begin set @Retorno = 0 commit tran nParametrosTipoCotizante end else begin set @Retorno = 1 rollback tran nParametrosTipoCotizante end