CREATE PROCEDURE [dbo].[SpActualizanPeriodo] @fechaInicial date,@empresa int,@año int,@periodosGenerado int,@diasPago int,@diasCorte int,@fechaRegistro datetime,@activo bit,@tipoNomina varchar(50),@usuario varchar(50),@Retorno int output  AS begin tran nPeriodo update nPeriodo set fechaInicial = @fechaInicial,periodosGenerado = @periodosGenerado,diasPago = @diasPago,diasCorte = @diasCorte,fechaRegistro = @fechaRegistro,activo = @activo,tipoNomina = @tipoNomina,usuario = @usuario where empresa = @empresa and año = @año if (@@error = 0 ) begin set @Retorno = 0 commit tran nPeriodo end else begin set @Retorno = 1 rollback tran nPeriodo end