CREATE PROCEDURE [dbo].[SpActualizanTipoConcepto] @empresa int,@activo bit,@codigo varchar(50),@descripcion varchar(550),@Retorno int output  AS begin tran nTipoConcepto update nTipoConcepto set activo = @activo,descripcion = @descripcion where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran nTipoConcepto end else begin set @Retorno = 1 rollback tran nTipoConcepto end