CREATE PROCEDURE [dbo].[SpActualizanTipoNomina] @empresa int,@fechaRegistro datetime,@activo bit,@codigo varchar(50),@descripcion varchar(550),@periocidad varchar(50),@observacion varchar(5550),@usuario nchar,@Retorno int output  AS begin tran nTipoNomina update nTipoNomina set fechaRegistro = @fechaRegistro,activo = @activo,descripcion = @descripcion,periocidad = @periocidad,observacion = @observacion,usuario = @usuario where empresa = @empresa and codigo = @codigo if (@@error = 0 ) begin set @Retorno = 0 commit tran nTipoNomina end else begin set @Retorno = 1 rollback tran nTipoNomina end