create PROCEDURE [dbo].[SpActualizanTurno] @empresa int,@horaInicio int,@horas int,@activo bit,@codigo varchar(50),@descripcion varchar(250),@Retorno int output  AS begin tran nTurno update nTurno set horaInicio = @horaInicio,horas = @horas,activo = @activo,descripcion = @descripcion where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran nTurno end else begin set @Retorno = 1 rollback tran nTurno end