
CREATE PROCEDURE [dbo].[SpActualizapJerarquiaAnalisis] @jerarquia int,@prioridad int,@empresa bit,@resultado bit,@discreta bit,@campoResul bit,@resultadoParcial bit,@resultadoFinal bit,@analisis varchar(10),@formula varchar(8000),@expresion varchar(8000),@Retorno int output  AS begin tran pJerarquiaAnalisis update pJerarquiaAnalisis set prioridad = @prioridad,resultado = @resultado,discreta = @discreta,campoResul = @campoResul,resultadoParcial = @resultadoParcial,resultadoFinal = @resultadoFinal,formula = @formula,expresion = @expresion where analisis = @analisis and empresa = @empresa and jerarquia = @jerarquia if (@@error = 0 ) begin set @Retorno = 0 commit tran pJerarquiaAnalisis end else begin set @Retorno = 1 rollback tran pJerarquiaAnalisis end