CREATE PROCEDURE SpActualizasMenu @activo bit,@codigo varchar(150),@modulo varchar(150),@pagina varchar(550),@descripcion varchar(550),@Retorno int output  AS begin tran sMenu update sMenu set activo = @activo,pagina = @pagina,descripcion = @descripcion where codigo = @codigo and modulo = @modulo if (@@error = 0 ) begin set @Retorno = 0 commit tran sMenu end else begin set @Retorno = 1 rollback tran sMenu end