CREATE PROCEDURE SpDeleteaLotePesosPeriodo @empresa int,@año int,@mes int,@finca varchar(50),@lote varchar(50),@Retorno int output  AS begin tran aLotePesosPeriodo delete aLotePesosPeriodo where año = @año and empresa = @empresa and finca = @finca and lote = @lote and mes = @mes if (@@error = 0 ) begin set @Retorno = 0 commit tran aLotePesosPeriodo end else begin set @Retorno = 1 rollback tran aLotePesosPeriodo end