CREATE PROCEDURE [dbo].[SpDeleteaTransaccionTercero] @empresa int,@tipo varchar(50),@numero varchar(50),@Retorno int output  AS begin tran aTransaccionTercero delete aTransaccionTercero where empresa = @empresa  and numero = @numero and tipo = @tipo if (@@error = 0 ) begin set @Retorno = 0 commit tran aTransaccionTercero end else begin set @Retorno = 1 rollback tran aTransaccionTercero end