CREATE PROCEDURE SpDeletebTipoVehiculo @empresa int,@codigo varchar(50),@Retorno int output  AS begin tran bTipoVehiculo delete bTipoVehiculo where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran bTipoVehiculo end else begin set @Retorno = 1 rollback tran bTipoVehiculo end