create PROCEDURE [dbo].[SpDeletecClaseParametroContaNomi] @empresa int,@codigo varchar(50),@Retorno int output  AS begin tran cClaseParametroContaNomi delete cClaseParametroContaNomi where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran cClaseParametroContaNomi end else begin set @Retorno = 1 rollback tran cClaseParametroContaNomi end