create PROCEDURE [dbo].[SpDeletecPlanCuenta] @empresa int,@codigo varchar(50),@Retorno int output  AS begin tran cPlanCuenta delete cPlanCuenta where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran cPlanCuenta end else begin set @Retorno = 1 rollback tran cPlanCuenta end