CREATE PROCEDURE [dbo].[SpDeletenCantroTrabajo] @empresa int,@codigo varchar(50),@Retorno int output  AS begin tran nCantroTrabajo delete nCantroTrabajo where empresa = @empresa and codigo = @codigo if (@@error = 0 ) begin set @Retorno = 0 commit tran nCantroTrabajo end else begin set @Retorno = 1 rollback tran nCantroTrabajo end