CREATE PROCEDURE [dbo].[SpDeletenCentroTrabajo] @empresa int,@codigo varchar(50),@Retorno int output  AS begin tran nCentroTrabajo delete nCentroTrabajo where empresa = @empresa and codigo = @codigo if (@@error = 0 ) begin set @Retorno = 0 commit tran nCentroTrabajo end else begin set @Retorno = 1 rollback tran nCentroTrabajo end