CREATE PROCEDURE [dbo].[SpDeletenConceptosFijos] @empresa int,@año int,@mes int,@noPeriodo int,@centroCosto varchar(50),@Retorno int output  AS begin tran nConceptosFijos delete nConceptosFijos where año = @año and centroCosto = @centroCosto and empresa = @empresa and mes = @mes and noPeriodo = @noPeriodo if (@@error = 0 ) begin set @Retorno = 0 commit tran nConceptosFijos end else begin set @Retorno = 1 rollback tran nConceptosFijos end