CREATE PROCEDURE [dbo].[SpDeletenConceptosFijosDetalle] @empresa int,@año int,@mes int,@noPeriodo int,@centroCosto varchar(50),@concepto varchar(50),@Retorno int output  AS begin tran nConceptosFijosDetalle delete nConceptosFijosDetalle where año = @año and centroCosto = @centroCosto and concepto = @concepto and empresa = @empresa and mes = @mes and noPeriodo = @noPeriodo if (@@error = 0 ) begin set @Retorno = 0 commit tran nConceptosFijosDetalle end else begin set @Retorno = 1 rollback tran nConceptosFijosDetalle end