
create PROCEDURE [dbo].[SpDeletenDiasHabilesCc] @empresa int,@ccosto varchar(50),@Retorno int output  AS begin tran nDiasHabilesCc delete nDiasHabilesCc where ccosto = @ccosto and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran nDiasHabilesCc end else begin set @Retorno = 1 rollback tran nDiasHabilesCc end