CREATE PROCEDURE [dbo].[SpDeletenEntidadCaja] @empresa int,@codigo varchar(50),@Retorno int output  AS begin tran nEntidadCaja delete nEntidadCaja where empresa = @empresa and codigo = @codigo if (@@error = 0 ) begin set @Retorno = 0 commit tran nEntidadCaja end else begin set @Retorno = 1 rollback tran nEntidadCaja end