CREATE PROCEDURE [dbo].[SpDeletenEntidadEps] @empresa int,@codigo varchar(50),@Retorno int output  AS begin tran nEntidadEps delete nEntidadEps where empresa = @empresa and codigo = @codigo if (@@error = 0 ) begin set @Retorno = 0 commit tran nEntidadEps end else begin set @Retorno = 1 rollback tran nEntidadEps end