create PROCEDURE [dbo].[SpDeletenGrupoConceptoDetalle] @empresa int,@grupo char(5),@cocepto char(5),@Retorno int output  AS begin tran nGrupoConceptoDetalle delete nGrupoConceptoDetalle where cocepto = @cocepto and empresa = @empresa and grupo = @grupo if (@@error = 0 ) begin set @Retorno = 0 commit tran nGrupoConceptoDetalle end else begin set @Retorno = 1 rollback tran nGrupoConceptoDetalle end