CREATE PROCEDURE [dbo].[SpDeletenParametroSeguridadSocial] @empresa int,@codigo varchar(50),@Retorno int output  AS begin tran nParametroSeguridadSocial delete nParametroSeguridadSocial where empresa = @empresa and codigo = @codigo if (@@error = 0 ) begin set @Retorno = 0 commit tran nParametroSeguridadSocial end else begin set @Retorno = 1 rollback tran nParametroSeguridadSocial end