CREATE PROCEDURE [dbo].[SpDeletenParametroSeguridadSocialDetalle] @empresa int,@codigo varchar(50),@concepto varchar(50),@Retorno int output  AS begin tran nParametroSeguridadSocialDetalle delete nParametroSeguridadSocialDetalle where concepto = @concepto and empresa = @empresa and codigo = @codigo if (@@error = 0 ) begin set @Retorno = 0 commit tran nParametroSeguridadSocialDetalle end else begin set @Retorno = 1 rollback tran nParametroSeguridadSocialDetalle end