create PROCEDURE [dbo].[SpDeletenSeguridadSocial] @empresa int,@año int,@mes int,@registro int,@Retorno int output  AS begin tran nSeguridadSocial delete nSeguridadSocial where año = @año and empresa = @empresa and mes = @mes and registro = @registro if (@@error = 0 ) begin set @Retorno = 0 commit tran nSeguridadSocial end else begin set @Retorno = 1 rollback tran nSeguridadSocial end