CREATE PROCEDURE [dbo].[SpDeletenTipoCotizante] @empresa int,@codigo varchar(50),@Retorno int output  AS begin tran nTipoCotizante delete nTipoCotizante where empresa = @empresa and codigo = @codigo if (@@error = 0 ) begin set @Retorno = 0 commit tran nTipoCotizante end else begin set @Retorno = 1 rollback tran nTipoCotizante end