create PROCEDURE [dbo].[SpDeletenVacacionesDetalle] @periodoInicial date,@periodoFinal date,@empresa int,@empleado int,@registro int,@Retorno int output  AS begin tran nVacacionesDetalle delete nVacacionesDetalle where empleado = @empleado and empresa = @empresa and periodoFinal = @periodoFinal and periodoInicial = @periodoInicial and registro = @registro if (@@error = 0 ) begin set @Retorno = 0 commit tran nVacacionesDetalle end else begin set @Retorno = 1 rollback tran nVacacionesDetalle end