create PROCEDURE [dbo].[SpDeletepJerarquia] @codigo int,@empresa int,@Retorno int output  AS begin tran pJerarquia delete pJerarquia where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran pJerarquia end else begin set @Retorno = 1 rollback tran pJerarquia end