CREATE PROCEDURE SpInsertabTipoVehiculo @empresa int,@aplicaRemolque bit,@activo bit,@codigo varchar(50),@descripcion varchar(250),@Retorno int output  AS begin tran bTipoVehiculo insert bTipoVehiculo( empresa,aplicaRemolque,activo,codigo,descripcion ) select @empresa,@aplicaRemolque,@activo,@codigo,@descripcion if (@@error = 0 ) begin set @Retorno = 0 commit tran bTipoVehiculo end else begin set @Retorno = 1 rollback tran bTipoVehiculo end