CREATE PROCEDURE [dbo].[SpInsertacCentrosCosto] @empresa int,@nivel int,@nivelMayor int,@activo bit,@auxiliar bit,@codigo varchar(50),@mayor varchar(50),@descripcion varchar(350),@responsable varchar(150),@grupo varchar(50),@manejaHE bit, @manejaLC bit, @Retorno int output  AS begin tran cCentrosCosto insert cCentrosCosto( empresa,nivel,nivelMayor,activo,auxiliar,codigo,mayor,descripcion,responsable,grupo, manejaHE, manejaLC ) select @empresa,@nivel,@nivelMayor,@activo,@auxiliar,@codigo,@mayor,@descripcion,@responsable,@grupo,@manejaHE,@manejaLC if (@@error = 0 ) begin set @Retorno = 0 commit tran cCentrosCosto end else begin set @Retorno = 1 rollback tran cCentrosCosto end