CREATE PROCEDURE [dbo].[SpInsertacClaseParametroContaNomi] @empresa int,@porTercero bit,@porCuenta bit,@porCentroCosto bit,@activo bit,@codigo varchar(50),@descripcion varchar(500),@tipo varchar(50),@tipoDocumento varchar(50),@comprobante varchar(50),@cuentaPuente varchar(50),@cuentaCruce varchar(50),@ccostoMayor varchar(50),@ccosto varchar(50),@Retorno int output  AS begin tran cClaseParametroContaNomi insert cClaseParametroContaNomi( empresa,porTercero,porCuenta,porCentroCosto,activo,codigo,descripcion,tipo,tipoDocumento,comprobante,cuentaPuente,cuentaCruce,ccostoMayor,ccosto ) select @empresa,@porTercero,@porCuenta,@porCentroCosto,@activo,@codigo,@descripcion,@tipo,@tipoDocumento,@comprobante,@cuentaPuente,@cuentaCruce,@ccostoMayor,@ccosto if (@@error = 0 ) begin set @Retorno = 0 commit tran cClaseParametroContaNomi end else begin set @Retorno = 1 rollback tran cClaseParametroContaNomi end