CREATE PROCEDURE SpInsertacConceptoIR @empresa int,@clase int,@baseMinima money,@baseGravable float,@tasa float,@activo bit,@codigo varchar(50),@descripcion varchar(950),@calculo char(1),@Retorno int output  AS begin tran cConceptoIR insert cConceptoIR( empresa,clase,baseMinima,baseGravable,tasa,activo,codigo,descripcion,calculo ) select @empresa,@clase,@baseMinima,@baseGravable,@tasa,@activo,@codigo,@descripcion,@calculo if (@@error = 0 ) begin set @Retorno = 0 commit tran cConceptoIR end else begin set @Retorno = 1 rollback tran cConceptoIR end