CREATE PROCEDURE SpInsertacContacto @empresa int,@idtercero int,@fechaRegistro datetime,@contacto varchar(550),@direccion varchar(950),@barrio varchar(250),@telefono varchar(50),@fax varchar(50),@email varchar(250),@codigo char(4),@ciudad char(5),@Retorno int output  AS begin tran cContacto insert cContacto( empresa,idtercero,fechaRegistro,contacto,direccion,barrio,telefono,fax,email,codigo,ciudad ) select @empresa,@idtercero,@fechaRegistro,@contacto,@direccion,@barrio,@telefono,@fax,@email,@codigo,@ciudad if (@@error = 0 ) begin set @Retorno = 0 commit tran cContacto end else begin set @Retorno = 1 rollback tran cContacto end