CREATE PROCEDURE SpInsertagParametros @empresa int,@codigo varchar(50),@descripcion varchar(250),@valor varchar(999),@Retorno int output  AS begin tran gParametros insert gParametros( empresa,codigo,descripcion,valor ) select @empresa,@codigo,@descripcion,@valor if (@@error = 0 ) begin set @Retorno = 0 commit tran gParametros end else begin set @Retorno = 1 rollback tran gParametros end